
public class Operators {
	
	int a=11;
	
	double b=9;
	
	public void add() {
		
		double c;
		
		c=a+b;
		
		System.out.println(c);
	}
	
	public void subtract() {
		
		double p;
		
		p=a-b;
		
		System.out.println(p);
	}


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Operators ob = new Operators();
		
		ob.add();
		ob.subtract();
	}
	
}
